mapper.select:
		
			select cateNo, id, cateName, description, regDate
			from category
			where cateNo = #{cateNo}

		
			select cateNo, cateName
			from category
			where id = #{id}

mapper.delete:

	DELETE FROM (select  c.cateNo,
	                     (select  count(case when p.cateNo = c.cateNo then 1 end)
	                      from post p) pNum
	 			 from category c)
	 WHERE pNum = 0
	 and cateNo = #{cateNo}
